import React, { Component } from 'react'

export default class Table extends Component {
    renderTableHeader = (header)=>{
        return header.map((val, index)=>{
            return <th key={index}>{val.toUpperCase()}</th>
        })
    }

    renderTableData = (header)=>{
        return this.props.data.map((val, index)=>{
            return(
            <tr key = {index} onClick= {(evt)=>{
                const index = evt.target.parentNode.rowIndex;
                if(index > 0)
                    this.props.onClickRow(this.props.data[index - 1]);   
            }}>
                header.map((elm)=>(
                    <td>{elm}</td>
                )
            </tr>
            )
        })
    }

    render() {
        if(this.props.data.length === 0)
        return <div></div>
        else{
        let header = Object.getOwnPropertyNames(this.props.data[0]);
            return(
                <div>
                    <table className="table" >
                        <tbody>
                        <tr>{this.renderTableHeader(header)}</tr>
                        {
                        this.renderTableData(header)
                        }
                    </tbody>
                </table>
                </div>
            );
        }
    }
}
