import React, { Component } from 'react';
import { url } from '../../config';
import DropdownWrapper from '../../components/DropdownWrapper';

export default class StudentList extends Component {
    state = {
        currentClass: "Class",
        currentYear: "Year",
        currentPart: "Part",
        class: []
    }

    componentDidMount() {
        fetch(url + 'class')
            .then(res => (
                res.json()
            ))
            .then(json => {
                this.setState({
                    ...this.state,
                    class: json.map((val)=> val.name)
                });
    
                return json;
            })
            .catch(err => {
                console.log(err);
            });
    }
    render() {
        console.log(this.state.class);
        return (
            <div className="d-flex justify-content-start">
                <DropdownWrapper 
                    title="Class"
                    data = {this.state.class.Select(x => x.name).ToArray()}
                />
            </div>
        )
    }
}
