import React, { Component } from 'react'
import { url } from '../../config'
export default class Report extends Component {

    state = {
        header : [],
        data : []
    };

    componentDidMount() {
        const { data } = this.props.location;
        const classId = data.class;
        const subjectCode = data.subjectCode;
        fetch(url + `attendance/all/${classId}/${subjectCode}`)
            .then(res => {
                return res.json();
            })
            .then(json => {
                const attendance = json[0].date.split(',');
                this.setState({
                    header:['Roll', ...attendance],
                    data:json.map((val, index)=>{
                        const status = val.status.split(',');
                        return [val.rollNo, ...status];
                    })
                })
                return json;
            })
            .catch(err => {
                console.log(err);
                return err;
            });
    }
    
    renderTableHeader = (cols) => (
        cols.map((val)=>{
            return val[0];
        })
    )

    renderTableData = (data, index)=>{
        return(<tr key={index}>
        </tr>);
    }
    
    render() {
        return (
            <div>
                <table className="table">
                    <tbody>
                        <tr>
                        {
                            this.renderTableHeader(this.state.header)
                        }
                        </tr>
                        {
                            this.state.data.map((val, index)=>{
                                return this.renderTableData(val, index);
                            })
                        }
                    </tbody>
                </table>
            </div>
        )
    }
}
