import React, { Component } from 'react';
import Dropdown from 'react-bootstrap/Dropdown';
import { url } from '../../config';

export default class StudentList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            currentClass: "Class",
            currentYear: "Year",
            currentPart: "Part",
        }
    }

    componentDidMount() {
        fetch(url + 'class')
            .then(res => {
                return res.json();
            })
            .then(json => {
                console.log(json);
                this.setState({
                    ...this.state,
                    year:json
                });
                return json;
            });
    }
    render() {
        return (
            <div className="d-flex justify-content-start">
                <Dropdown className="mt-3">
                    <Dropdown.Toggle className="mr-3" variant="success" id="dropdown-basic">
                        {this.state.currentClass}
                    </Dropdown.Toggle>
                    <Dropdown.Menu>
                        {
                            this.state.year.map((val, index) => <Dropdown.Item>{this.val.name}</Dropdown.Item>)
                        }
                    </Dropdown.Menu>
                </Dropdown>
                <Dropdown className="mt-3">
                    <Dropdown.Toggle className="mr-3" variant="success" id="dropdown-basic">
                        {this.state.currentYear}
                    </Dropdown.Toggle>
                    <Dropdown.Menu>
                        {
                            this.state.year.map((val, index) => <Dropdown.Item>{this.val.year}</Dropdown.Item>)
                        }
                    </Dropdown.Menu>
                </Dropdown>
                <Dropdown className="mt-3">
                    <Dropdown.Toggle className="mr-3" variant="success" id="dropdown-basic">
                        {this.state.currentPart}
                    </Dropdown.Toggle>
                    <Dropdown.Menu>
                        {
                            this.state.year.map((val, index) => <Dropdown.Item>{this.val.year}</Dropdown.Item>)
                        }
                    </Dropdown.Menu>
                </Dropdown>
            </div>
        )
    }
}
