            return response.json();
        })
        .then(json=>{
            this.setState({
                data:json,
            });
        });
        */        
    }

    render() {
        return (
            <div>
                <h5>Class: {this.props.data.class} </h5>
                 <h5>Date: {this.props.data.date.split('T')[0]}  </h5>
                <h5>Subject: {this.props.data.subject}</h5>
               <Table data={this.state.data} onClickRow={(index)=>{}}/>
            </div>
        )
    }
}
