import React, { Component } from 'react';
import { url } from '../../config';
import DropdownWrapper from '../../components/DropdownWrapper';
import CustomTable from '../../components/CustomTable';
export default class StudentList extends Component {
    state = {
        currentClass: "Class",
        currentYear: "Year",
        currentPart: "Part",
        class: [],
        students:[]
    }

    componentDidMount() {
        fetch(url + 'class')
            .then(res => (
                res.json()
            ))
            .then(json => {
                console.log(json.map(val=>val.name));
                this.setState({
                    ...this.state,
                    class: json.map(val=> val.name)
                });
                return json;
            })
            .catch(err => {
                console.log(err);
            });
    }

    handleSelection = (val)=>{
        fetch(url + `class/students/${val}`)
        .then(res=>res.json())
        .then(json=>{
            this.setState({
                ...this.state,
                currentClass: val,
                students:json
            });
        })
    }

    render() {
        return (
            <div className="d-flex justify-content-start">
                <DropdownWrapper 
                    onSelect = {this.handleSelection}
                    title= {this.state.currentClass}
                    data = {this.state.class}
                />{
                    this.state.students.length > 0 ? <CustomTable /> : <div></div>
                }
            </div>
        )
    }
}
