import React, { Component } from 'react'
import {url} from '../../config'
import Default from '../pages/Default'
import CustomTable from '../CustomTable'

export default class SubjectList extends Component {
    state ={
        data:[],
    }
    componentDidMount(){
        const data = this.props.location.state;
        fetch(url + `subject/${data.name}`)
        .then(res=>{
            return res.json();
        })
        .then(json=>{
            this.setState({
                data:json,
                className:data.name
            })
            return json;
        })
        .catch(err=>{
            console.log(err);
        })
    }
    render() {
        const {className} = this.state;     
        return (
            this.state.data.length === 0 ? <Default/> : 
            <div className="container-fluid">
                <div style={headingContainerStyle}>
                <h6>Class: {className} </h6>
                <h4 className="text-center">SubjectList</h4>
                </div>
                <CustomTable header = {Object.getOwnPropertyNames(this.state.data[0])} data = {this.state.data}  onClickRow={(index)=>{
                     this.props.onClickRow({...this.state.data[index], class:className});
                 }}/>
                {
                    /*
                 data.map((val, index)=>{
                     return <li key={index}>{val.name}</li>
                 })   
                 */
                }
            </div>
        )
    }
}

