import React from 'react';
import './App.css';
import Header from './components/layout/header';

import { BrowserRouter as Router, Route } from 'react-router-dom';

import Home from './components/pages/Home';
import InstructorList from './components/pages/InstructorList';
import ClassList from './components/pages/ClassList';
import SubjectList from './components/pages/SubjectList'
import Report from './components/pages/Report'

//@Todo pass the data from link

function App() {
  return (
    <Router>
      <div className="App">
        <Header />

        <Route exact path='/' render={props => (
          <Home onClickRow={(subjectDetail) => {
            props.history.push({
              pathname: `/report`,
              state: subjectDetail
            });
          }} />
        )} />

        <Route path='/subjectlist' render={props => (
          <SubjectList onClickRow={(subjectDetail) => {
            props.history.push({
              pathname: '/report',
              state: subjectDetail
            })
          }} />
        )} />
        <Route path='/classes' render={props => (
          <ClassList onClickRow={(classDetail) => {
            props.history.push({
              pathname: '/subjectlist',
              state: classDetail
            })
          }} />
        )} />


        <Route path='/instructor' component={InstructorList} />
        <Route path='/report' component={Report} />
      </div>
    </Router>
  );
}

export default App;
