import React, { Component } from 'react'

export default class Table extends Component {
    renderTableHeader = ()=>{
        let header = Object.getOwnPropertyNames(this.props.data[0]);
        return header.map((val, index)=>{
            return <th key={index}>{val.toUpperCase()}</th>
        })
    }

    renderTableData = ()=>{
        return this.props.data.map((val, index)=>{
            const{Class, Date, Instructor, Subject} = val;
            return(
            <tr key = {index} onClick= {(evt)=>{
                const index = evt.target.parentNode.rowIndex;   
            }}>
                <td>{Class}</td>
                <td>{Date.split("T")[0]}</td>
                <td>{Instructor}</td>
                <td>{Subject}</td>
            </tr>
            )
        })
    }

    onClick = (evt)=>{
        const index = evt.target.parentNode.rowIndex;
        console.log(this.props.data[index - 1]);
    }
    render() {
        if(this.props.data.length === 0)
            return <div></div>
        else
            return(
                <div>
                    <table className="table" >
                        <tbody>
                        <tr>{this.renderTableHeader()}</tr>
                        {
                        this.renderTableData()
                        }
                    </tbody>
                </table>
                </div>
            );
    }
}
