import React, { Component } from 'react'
import Table from '../table'
import {url} from '../../config.js'

export default class Home extends Component {
    state = {
        data:[]
    };
    
    componentDidMount(){
      
        fetch(url +'attendance/getRecent/30')
        .then(response=>{
            return response.json();
        })
        .then(json=>{
            this.setState({
                data:json
            })
        })
    }
    render() {
        return (
            <div style={bodyStyle}>
            <h2 style={titleStyle}>Recently Added</h2>
            <Table data = {this.state.data} onClickRow={(index)=>this.props.onClickRow(this.states.data[index])}/>
        </div>
        )
    }
}

const bodyStyle = {
};

const titleStyle = {
    textAlign:'center',
    color:'#3d3d3d',
    marginTop:'30px'

};
