import React, { Component } from 'react'

export default class Table extends Component {
    renderTableHeader = ()=>{
        let header = Object.getOwnPropertyNames(this.props.data[0]);
        return header.map((val, index)=>{
            return <th key={index}>{val.toUpperCase()}</th>
        })
    }

    renderTableData = ()=>{
        return this.props.data.map((val, index)=>{
            const{Class, Date, Instructor, Subject} = val;
            return(
            <tr key = {index} onClick= {this.onClick}>
                <td>{Class}</td>
                <td>{Date}</td>
                <td>{Instructor}</td>
                <td>{Subject}</td>
            </tr>
            )
        })
    }

    onClick = (evt)=>{
        console.log('%O',evt.target);
    }
    render() {
        return(
            <div>
                <table className="table" >
                    <tbody>
                     <tr>{this.renderTableHeader()}</tr>
                     {
                      this.renderTableData()
                     }
                </tbody>
            </table>
            </div>
        );
    }
}
