import React, { Component } from 'react'
import { Bar } from 'react-chartjs-2';

export default class BarWrapper extends Component {

    static defaultProps = {
        defaultOptions: {
            responsive: true,
            maintainAspectRatio: true,
            title: {
                display: true,
                text: "Attendance on all Subject",
                fontSize: 25
            },
            legend: {
                display: true,
                position: 'right'
            },
            scales:{
                xAxes:[{
                    barPercentage:0.5
                }],
                yAxes:[{
                    ticks:{
                        beginAtZero:true,
                    }
                }]
            }
        }
    }
    render() {
        return (
            <div className="container-fluid border border-color-dark w-60 h-60">
                <Bar
                    data={{
                        labels: this.props.data.map(val => val.subject),
                        datasets: [{
                            label: 'Total',
                            backgroundColor: 'rgb(155, 99, 132)',
                            borderColor: 'rgb(255, 99, 132)',
                            data: this.props.data.map(val=>val.totalDay)
                        },
                        {
                            label: 'Present',
                            backgroundColor: 'rgb(99, 255, 132)',
                            borderColor: 'rgb(255, 99, 132)',
                            data: this.props.data.map(val=>val.present)
                        },
                        {
                            label: 'Absent',
                            backgroundColor: 'rgb(255, 99, 132)',
                            borderColor: 'rgb(255, 99, 132)',
                            data: this.props.data.map(val=>val.total-val.present)
                        },
                        ]
                    }
                    }
                    options={this.props.defaultOptions}
                />
            </div>
        )
    }
}
