import React, { Component } from 'react';
import { url } from '../../config';
import CustomTable from '../CustomTable';
import Default from '../pages/Default'
import { Bar } from 'react-chartjs-2';

export default class Student extends Component {
    state = {
        data: []
    }
    componentDidMount() {
        const { rollno } = this.props.location.state;
        fetch(url + `student/${rollno}`)
            .then(res => res.json())
            .then(json => {
                this.setState({
                    data: json
                });
                return json;
            })
            .catch((err) => {
                console.log(err);
            })
    }

    render() {
        const { rollno, name } = this.props.location.state;
        return (
            <div>
                <div className="container-fluid">
                    <p className="mt-3"><b>Roll no: </b>{rollno}</p>
                    <p style={subheading}><b>Name: </b>{name}</p>
                </div>
                {
                    this.state.data.length > 0 ? (
                        <div className="container-fluid">
                            <Bar
                                data={{
                                    labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
                                    datasets: [{
                                        label: 'Subject',
                                        backgroundColor: 'rgb(255, 99, 132)',
                                        borderColor: 'rgb(255, 99, 132)',
                                        data: [0, 10, 5, 2, 20, 30, 45]
                                    }]
                                }
                                }
                                options={{
                                    title: {
                                        display: true,
                                        text: "Attendance on all Subject",
                                        fontSize: 25
                                    },
                                    legend:{
                                        display:true,
                                        position:'right'
                                    }
                                }}
                            />
                            <CustomTable header={Object.getOwnPropertyNames(this.state.data[0])}
                                data={this.state.data}
                                onClickRow={() => { }}
                            />
                        </div>
                    ) : <Default />

                }
            </div>
        )
    }
}

const subheading = {
    lineHeight: "5px"
}
